package jp.rough_diamond.commons.resource;import java.util.Locale;/** * JavaVM毎にローエルを管理するローケルコントローラー * ローケルがセットされていない場合はデフォルトローケルを返却する * @author e-yamane */public class SimpleLocaleController extends LocaleController {    private Locale locale = Locale.getDefault();    @Override    public Locale getLocale() {        return locale;    }    @Override    public void setLocale(Locale locale) {        Locale old = getLocale();        if(!old.equals(locale)) {            this.locale = locale;            System.out.println("デフォルトローケルの変更：" + locale);            Locale.setDefault(locale);        }    }}